mapper.select:
select c.id, c.customerId, c.name, c.code, c.creatDate, c.userId, c.contactId, c.money, c.startDate, c.endDate, c.status
,cu.company as customerName,co.name as contactName,SUM(p.money) AS payment
from sys_contract c
left join sys_customer cu on cu.id=c.customerId
left join sys_contact co on co.id=c.userId
left join sys_contract_payment p on p.contractId=c.id


        and c.userId in

            #{item}



        and c.id = #{query.id}


        and c.customerId = #{query.customerId}


        and c.name = #{query.name}


        and c.code = #{query.code}


        and c.creatDate = #{query.creatDate}


        and c.userId = #{query.userId}


        and c.contactId = #{query.contactId}


        and c.money = #{query.money}


        and c.remark = #{query.remark}


        and c.files = #{query.files}


        and c.startDate = #{query.startDate}


        and c.endDate = #{query.endDate}


        and c.status = #{query.status}


GROUP BY c.id


        order by ${extend.sort}


        order by c.id desc



    limit #{extend.pageIndex}, #{extend.pageSize}

select count(1)
from sys_contract c


        and c.userId in

            #{item}



        and c.id = #{query.id}


        and c.customerId = #{query.customerId}


        and c.name = #{query.name}


        and c.code = #{query.code}


        and c.creatDate = #{query.creatDate}


        and c.userId = #{query.userId}


        and c.contactId = #{query.contactId}


        and c.money = #{query.money}


        and c.remark = #{query.remark}


        and c.files = #{query.files}


        and c.startDate = #{query.startDate}


        and c.endDate = #{query.endDate}


        and c.status = #{query.status}

