mapper.select:
select
a.id,a.customerId, a.contactId, a.contactName, a.userId, a.userName, a.dateTime, a.type, a.remark, b.company
from sys_follow_records as a
join sys_customer as b on a.customerId = b.id


        and b.userId=#{extend.userId}


        and b.userId in

            #{item}



        and a.id = #{query.id}


        and a.customerId = #{query.customerId}


        and a.contactId = #{query.contactId}


        and a.contactName = #{query.contactName}


        and a.userId = #{query.userId}


        and a.userName = #{query.userName}


        and a.dateTime = #{query.dateTime}


        and a.type = #{query.type}


        and a.remark = #{query.remark}




        order by ${extend.sort}


        order by id desc



    limit #{extend.pageIndex}, #{extend.pageSize}

select count(1)
from sys_follow_records as a
join sys_customer as b on a.customerId = b.id


        and b.userId=#{extend.userId}


        and b.userId in

            #{item}



        and a.id = #{query.id}


        and a.customerId = #{query.customerId}


        and a.contactId = #{query.contactId}


        and a.contactName = #{query.contactName}


        and a.userId = #{query.userId}


        and a.userName = #{query.userName}


        and a.dateTime = #{query.dateTime}


        and a.type = #{query.type}


        and a.remark = #{query.remark}

