mapper.select:
select
u.id, userName, u.status, creatTime, updateTime, lastLogin, loginTimer, ip, u.remark, departmentId,d.name as departmentName, u.tid, phone, weixin, qq, sex, roleId,bindWX
from sys_user as u join sys_department as d on departmentId=d.id


            and u.id = #{query.id}


            and userName = #{query.userName}


            and u.status = #{query.status}


            and creatTime = #{query.creatTime}


            and updateTime = #{query.updateTime}


            and lastLogin = #{query.lastLogin}


            and loginTimer = #{query.loginTimer}


            and ip = #{query.ip}


            and u.remark = #{query.remark}


            and departmentId = #{query.departmentId}


            and u.tid = #{query.tid}


            and phone = #{query.phone}


            and weixin = #{query.weixin}


            and qq = #{query.qq}


            and sex = #{query.sex}


            and FIND_IN_SET(#{query.roleId}, roleId) > 0




        order by ${extend.sort}


        order by id desc



limit #{extend.pageIndex}, #{extend.pageSize}

select count(1)
from sys_user


            and id = #{id}


            and userName = #{userName}



            and status = #{status}


            and creatTime = #{creatTime}


            and updateTime = #{updateTime}


            and lastLogin = #{lastLogin}


            and loginTimer = #{loginTimer}


            and ip = #{ip}


            and remark = #{remark}


            and departmentId = #{departmentId}


            and tid = #{tid}


            and phone = #{phone}


            and weixin = #{weixin}


            and qq = #{qq}


            and sex = #{sex}


            and roleId = #{roleId}

mapper.delete:
delete
from sys_user where id in

#{item}

mapper.select:
SELECT u.id,u.userName,COUNT(DISTINCT c.id) AS customerCount, COUNT(DISTINCT CASE WHEN con.id IS NOT NULL THEN c.id END) AS contractCount FROM sys_user as u LEFT JOIN sys_customer c ON u.id = c.userId LEFT JOIN sys_contract con ON c.id = con.customerId and con.status=1 WHERE u.status=1 and c.status=1 and (u.id=#{userId} or u.id in
#{item}
) GROUP BY u.id, u.userName ORDER BY customerCount DESC

SELECT u.id AS userId, u.userName, COUNT(DISTINCT f.customerId) AS followedCustomerCount, -- 跟进的客户数量（去重） COUNT(f.id) AS followUpCount, -- 跟进次数 (SELECT COUNT(*) FROM sys_customer c WHERE c.userId = u.id) AS totalCustomers, -- 总客户数 MAX(f.dateTime) AS lastFollowUpTime -- 最后跟进时间 FROM sys_user u LEFT JOIN sys_customer c ON u.id = c.userId and c.status=1 LEFT JOIN sys_follow_records f ON c.id = f.customerId WHERE u.status=1 and (u.id = #{userId} or u.id in
#{item}
) GROUP BY u.id, u.userName;

SELECT u.id AS userId,u.userName,COUNT(c.id) AS count,COALESCE(SUM(c.money), 0) AS totalMoney,COALESCE(SUM(c.payment), 0) AS totalPayment FROM sys_user u LEFT JOIN sys_contract c ON u.id = c.userId AND CURRENT_DATE BETWEEN c.startDate AND c.endDate WHERE u.status=1 and (u.id=#{userId} or u.id in
#{item}
) GROUP BY u.id, u.userName ORDER BY totalMoney DESC
